import React, { useState } from 'react';

const PureReactPPOfficeAdminDashboard = () => {
  const [activeTab, setActiveTab] = useState('createStaff');
  const [newStaff, setNewStaff] = useState({
    fullName: '',
    userName: '',
    email: '',
    contactNumber: '',
    licenseNo: '',
    password: ''
  });
  const [passwordReset, setPasswordReset] = useState({ userId: '', newPassword: '' });
  const [caseAllocation, setCaseAllocation] = useState({ caseId: '', staffId: '' });

  // Mock data for demonstration
  const [ppStaff, setPPStaff] = useState([
    { id: 1, name: 'John Doe', email: 'john@example.com' },
    { id: 2, name: 'Jane Smith', email: 'jane@example.com' },
  ]);
  const [cases, setCases] = useState([
    { id: 'CASE001', title: 'Case 1' },
    { id: 'CASE002', title: 'Case 2' },
  ]);

  const handleCreateStaff = (e) => {
    e.preventDefault();
    const newStaffMember = { id: ppStaff.length + 1, ...newStaff };
    setPPStaff([...ppStaff, newStaffMember]);
    setNewStaff({ fullName: '', userName: '', email: '', contactNumber: '', licenseNo: '', password: '' });
    alert('New PP staff created successfully!');
  };

  const handlePasswordReset = (e) => {
    e.preventDefault();
    console.log('Password reset for user:', passwordReset);
    setPasswordReset({ userId: '', newPassword: '' });
    alert('Password reset successfully!');
  };

  const handleCaseAllocation = (e) => {
    e.preventDefault();
    console.log('Case allocated:', caseAllocation);
    setCaseAllocation({ caseId: '', staffId: '' });
    alert('Case allocated successfully!');
  };

  return (
    <div className="max-w-4xl mx-auto p-6 font-sans">
      <h1 className="text-3xl mb-6 text-gray-800">PP Office Admin Dashboard</h1>

      <div className="flex mb-6">
        <button 
          className={`px-6 py-2 bg-gray-100 hover:bg-gray-200 transition-colors ${activeTab === 'createStaff' ? 'bg-blue-500 text-white' : 'text-gray-700'}`}
          onClick={() => setActiveTab('createStaff')}
        >
          Create PP Staff
        </button>
        <button 
          className={`px-6 py-2 bg-gray-100 hover:bg-gray-200 transition-colors ${activeTab === 'resetPassword' ? 'bg-blue-500 text-white' : 'text-gray-700'}`}
          onClick={() => setActiveTab('resetPassword')}
        >
          Reset Password
        </button>
        <button 
          className={`px-6 py-2 bg-gray-100 hover:bg-gray-200 transition-colors ${activeTab === 'allocateCase' ? 'bg-blue-500 text-white' : 'text-gray-700'}`}
          onClick={() => setActiveTab('allocateCase')}
        >
          Allocate Case
        </button>
      </div>

      <div className="space-y-6">
        {activeTab === 'createStaff' && (
          <div className="bg-white rounded-lg shadow-lg p-6">
            <h2 className="text-2xl mb-4 text-gray-800">Create PP Staff</h2>
            <p className="text-sm text-gray-600 mb-4">Add a new staff member to the PP Office.</p>
            <form onSubmit={handleCreateStaff}>
              <div className="mb-4">
                <label htmlFor="fullName" className="block font-semibold text-gray-700">Full Name</label>
                <input
                  id="fullName"
                  value={newStaff.fullName}
                  onChange={(e) => setNewStaff({...newStaff, fullName: e.target.value})}
                  required
                  className="w-full px-4 py-2 border border-gray-300 rounded-md mt-2"
                />
              </div>
              <div className="mb-4">
                <label htmlFor="userName" className="block font-semibold text-gray-700">Username</label>
                <input
                  id="userName"
                  value={newStaff.userName}
                  onChange={(e) => setNewStaff({...newStaff, userName: e.target.value})}
                  required
                  className="w-full px-4 py-2 border border-gray-300 rounded-md mt-2"
                />
              </div>
              <div className="mb-4">
                <label htmlFor="email" className="block font-semibold text-gray-700">Email</label>
                <input
                  id="email"
                  type="email"
                  value={newStaff.email}
                  onChange={(e) => setNewStaff({...newStaff, email: e.target.value})}
                  required
                  className="w-full px-4 py-2 border border-gray-300 rounded-md mt-2"
                />
              </div>
              <div className="mb-4">
                <label htmlFor="contactNumber" className="block font-semibold text-gray-700">Contact Number</label>
                <input
                  id="contactNumber"
                  value={newStaff.contactNumber}
                  onChange={(e) => setNewStaff({...newStaff, contactNumber: e.target.value})}
                  required
                  className="w-full px-4 py-2 border border-gray-300 rounded-md mt-2"
                />
              </div>
              <div className="mb-4">
                <label htmlFor="licenseNo" className="block font-semibold text-gray-700">License No.</label>
                <input
                  id="licenseNo"
                  value={newStaff.licenseNo}
                  onChange={(e) => setNewStaff({...newStaff, licenseNo: e.target.value})}
                  required
                  className="w-full px-4 py-2 border border-gray-300 rounded-md mt-2"
                />
              </div>
              <div className="mb-4">
                <label htmlFor="password" className="block font-semibold text-gray-700">Password</label>
                <input
                  id="password"
                  type="password"
                  value={newStaff.password}
                  onChange={(e) => setNewStaff({...newStaff, password: e.target.value})}
                  required
                  className="w-full px-4 py-2 border border-gray-300 rounded-md mt-2"
                />
              </div>
              <button type="submit" className="w-full py-3 bg-blue-500 text-white rounded-md hover:bg-blue-600 transition">Create Staff</button>
            </form>
          </div>
        )}

        {activeTab === 'resetPassword' && (
          <div className="bg-white rounded-lg shadow-lg p-6">
            <h2 className="text-2xl mb-4 text-gray-800">Reset Password</h2>
            <p className="text-sm text-gray-600 mb-4">Reset password for yourself or PP staff members.</p>
            <form onSubmit={handlePasswordReset}>
              <div className="mb-4">
                <label htmlFor="userId" className="block font-semibold text-gray-700">Select User</label>
                <select
                  id="userId"
                  value={passwordReset.userId}
                  onChange={(e) => setPasswordReset({...passwordReset, userId: e.target.value})}
                  required
                  className="w-full px-4 py-2 border border-gray-300 rounded-md mt-2"
                >
                  <option value="">Select a user</option>
                  <option value="admin">Admin (You)</option>
                  {ppStaff.map(staff => (
                    <option key={staff.id} value={staff.id.toString()}>{staff.name}</option>
                  ))}
                </select>
              </div>
              <div className="mb-4">
                <label htmlFor="newPassword" className="block font-semibold text-gray-700">New Password</label>
                <input
                  id="newPassword"
                  type="password"
                  value={passwordReset.newPassword}
                  onChange={(e) => setPasswordReset({...passwordReset, newPassword: e.target.value})}
                  required
                  className="w-full px-4 py-2 border border-gray-300 rounded-md mt-2"
                />
              </div>
              <button type="submit" className="w-full py-3 bg-blue-500 text-white rounded-md hover:bg-blue-600 transition">Reset Password</button>
            </form>
          </div>
        )}

        {activeTab === 'allocateCase' && (
          <div className="bg-white rounded-lg shadow-lg p-6">
            <h2 className="text-2xl mb-4 text-gray-800">Allocate Case</h2>
            <p className="text-sm text-gray-600 mb-4">Assign cases to PP staff members.</p>
            <form onSubmit={handleCaseAllocation}>
              <div className="mb-4">
                <label htmlFor="caseId" className="block font-semibold text-gray-700">Select Case</label>
                <select
                  id="caseId"
                  value={caseAllocation.caseId}
                  onChange={(e) => setCaseAllocation({...caseAllocation, caseId: e.target.value})}
                  required
                  className="w-full px-4 py-2 border border-gray-300 rounded-md mt-2"
                >
                  <option value="">Select a case</option>
                  {cases.map(case_ => (
                    <option key={case_.id} value={case_.id}>{case_.title}</option>
                  ))}
                </select>
              </div>
              <div className="mb-4">
                <label htmlFor="staffId" className="block font-semibold text-gray-700">Assign to PP Staff</label>
                <select
                  id="staffId"
                  value={caseAllocation.staffId}
                  onChange={(e) => setCaseAllocation({...caseAllocation, staffId: e.target.value})}
                  required
                  className="w-full px-4 py-2 border border-gray-300 rounded-md mt-2"
                >
                  <option value="">Select a staff member</option>
                  {ppStaff.map(staff => (
                    <option key={staff.id} value={staff.id.toString()}>{staff.name}</option>
                  ))}
                </select>
              </div>
              <button type="submit" className="w-full py-3 bg-blue-500 text-white rounded-md hover:bg-blue-600 transition">Allocate Case</button>
            </form>
          </div>
        )}
      </div>
    </div>
  );
};

export default PureReactPPOfficeAdminDashboard;
